                    type="number"
                    min="15"
                    step="15"
                    value={authUser?.defaultSubtaskDuration || 60}
                    onChange={async (e) => {
                      if (!authUser) return;
                      const newDuration = parseInt(e.target.value);
                      try {
                        await api.updateUserPreference(authUser.id, newDuration);
